CREATE DEFINER=`root`@`localhost` TRIGGER `friend_count_incrementer1` AFTER INSERT ON `friend_add` FOR EACH ROW begin
update user set friend_count = (friend_count + 1) where userid = new.userid_1;
end


CREATE DEFINER=`root`@`localhost` TRIGGER `solarpanels_datetracker` AFTER INSERT ON `insert_history` FOR EACH ROW begin
if new.activity_name = "solarpanels" then
update user set date_added_solarpanels = NOW()
where userid = new.userid;
end if;
end


CREATE DEFINER=`root`@`localhost` TRIGGER `new_record_create` AFTER INSERT ON `user` FOR EACH ROW begin
insert into records(userid) values(new.userid);
end


CREATE DEFINER=`root`@`localhost` TRIGGER `new_friendlist_create` AFTER INSERT ON `user` FOR EACH ROW begin
insert into friend_list(src_user_id) values(new.userid);
end


CREATE DEFINER=`root`@`localhost` TRIGGER `new_achievements_create` AFTER INSERT ON `user` FOR EACH ROW begin
insert into achievements(userid) values(new.userid);
end
